import { useEffect, useState } from "react";
import axios from "axios";

const API = "http://localhost:8000";

export default function InventoryExitForm() {
  const [exit, setExit] = useState({ product_id: "", quantity: "" });
  const [products, setProducts] = useState([]);

  useEffect(() => {
    axios.get(`${API}/inventory`)
      .then((res) => setProducts(res.data))
      .catch(() => alert("Error al cargar productos"));
  }, []);

  const handleChange = (e) => {
    setExit({ ...exit, [e.target.name]: e.target.value });
  };

  const submitExit = async () => {
    if (!exit.product_id || !exit.quantity) {
      return alert("Todos los campos son obligatorios");
    }

    try {
      await axios.post(`${API}/inventory/exit`, {
        product_id: parseInt(exit.product_id),
        quantity: parseInt(exit.quantity),
      });

      alert("Salida registrada");
      setExit({ product_id: "", quantity: "" });
    } catch (err) {
      alert(err.response?.data?.detail || "Error al registrar salida");
    }
  };

  return (
    <div style={styles.container}>
      <h2>Registrar Salida</h2>

      <div style={styles.formGroup}>
        <label>Producto:</label>
        <select name="product_id" value={exit.product_id} onChange={handleChange}>
          <option value="">Seleccione un producto</option>
          {products.map((p) => (
            <option key={p.id} value={p.id}>
              {p.name} — {p.category || "Sin categoría"}
            </option>
          ))}
        </select>
      </div>

      <div style={styles.formGroup}>
        <label>Cantidad:</label>
        <input
          name="quantity"
          type="number"
          min="1"
          value={exit.quantity}
          onChange={handleChange}
        />
      </div>

      <button style={styles.button} onClick={submitExit}>
        Registrar Salida
      </button>
    </div>
  );
}

const styles = {
  container: {
    maxWidth: "400px",
    margin: "2rem auto",
    padding: "2rem",
    border: "1px solid #ccc",
    borderRadius: "8px",
    backgroundColor: "#f9f9f9",
    boxShadow: "0 0 10px rgba(0,0,0,0.1)",
  },
  formGroup: {
    marginBottom: "1rem",
    display: "flex",
    flexDirection: "column",
    textAlign: "left",
  },
  button: {
    padding: "0.5rem 1rem",
    fontWeight: "bold",
    backgroundColor: "#282c34",
    color: "#fff",
    border: "none",
    cursor: "pointer",
  },
};
