import { useState, useEffect } from "react";
import axios from "axios";

const API = "http://localhost:8000";

export default function InventoryEntryForm() {
  const [entry, setEntry] = useState({ product_id: "", quantity: "", expiration_date: "" });
  const [products, setProducts] = useState([]);

  useEffect(() => {
    axios.get(`${API}/inventory`).then((res) => {
      setProducts(res.data);
    });
  }, []);

  const handleChange = (e) => {
    setEntry({ ...entry, [e.target.name]: e.target.value });
  };

  const submitEntry = async () => {
    try {
      await axios.post(`${API}/inventory/entry`, {
        ...entry,
        product_id: parseInt(entry.product_id),
        quantity: parseInt(entry.quantity),
      });
      alert("Entrada registrada");
      setEntry({ product_id: "", quantity: "", expiration_date: "" });
    } catch (err) {
      alert(err.response?.data?.detail || "Error en entrada");
    }
  };

  return (
    <div style={styles.container}>
      <h2>Agregar Entrada</h2>

      <div style={styles.formGroup}>
        <label>Producto:</label>
        <select name="product_id" value={entry.product_id} onChange={handleChange}>
          <option value="">Seleccione un producto</option>
          {products.map((p) => (
            <option key={p.id} value={p.id}>
              {p.name} — {p.category || "Sin categoría"}
            </option>
          ))}
        </select>
      </div>

      <div style={styles.formGroup}>
        <label>Cantidad:</label>
        <input name="quantity" type="number" value={entry.quantity} onChange={handleChange} />
      </div>

      <div style={styles.formGroup}>
        <label>Fecha de Vencimiento:</label>
        <input name="expiration_date" type="date" value={entry.expiration_date} onChange={handleChange} />
      </div>

      <button style={styles.button} onClick={submitEntry}>
        Registrar
      </button>
    </div>
  );
}

const styles = {
  container: {
    maxWidth: "400px",
    margin: "2rem auto",
    padding: "2rem",
    border: "1px solid #ccc",
    borderRadius: "8px",
    backgroundColor: "#f9f9f9",
    boxShadow: "0 0 10px rgba(0,0,0,0.1)",
  },
  formGroup: {
    marginBottom: "1rem",
    display: "flex",
    flexDirection: "column",
    textAlign: "left",
  },
  button: {
    padding: "0.5rem 1rem",
    fontWeight: "bold",
    backgroundColor: "#282c34",
    color: "#fff",
    border: "none",
    cursor: "pointer",
  },
};
