import { useEffect, useState } from "react";
import axios from "axios";

const API = "http://localhost:8000";

export default function InventoryList() {
  const [products, setProducts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const perPage = 5;

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = () => {
    axios.get(`${API}/inventory`).then((res) => setProducts(res.data));
  };

  const handleDelete = async (id) => {
    if (!window.confirm("¿Estás seguro de eliminar este producto?")) return;

    try {
      await axios.delete(`${API}/products/${id}`);
      fetchProducts(); // Refresca lista
    } catch (error) {
      alert(error.response?.data?.detail || "Error al eliminar producto");
    }
  };

  const paginatedProducts = products.slice(
    (currentPage - 1) * perPage,
    currentPage * perPage
  );

  const totalPages = Math.ceil(products.length / perPage);

  return (
    <div style={styles.container}>
      <h2>Estado General del Inventario</h2>
      <table style={styles.table}>
        <thead>
          <tr>
            <th style={styles.th}>Nombre</th>
            <th style={styles.th}>Descripción</th>
            <th style={styles.th}>Categoría</th>
            <th style={styles.th}>Precio</th>
            <th style={styles.th}>Vigente</th>
            <th style={styles.th}>Por Vencer</th>
            <th style={styles.th}>Vencido</th>
            <th style={styles.th}>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {paginatedProducts.map((product) => (
            <tr key={product.id}>
              <td style={styles.td}>{product.name}</td>
              <td style={styles.td}>{product.description}</td>
              <td style={styles.td}>{product.category || "-"}</td>
              <td style={styles.td}>${product.price?.toFixed(2) || "-"}</td>
              <td style={styles.td}>{product.inventario.vigente}</td>
              <td style={styles.td}>{product.inventario.por_vencer}</td>
              <td style={styles.td}>{product.inventario.vencido}</td>
              <td style={styles.td}>
                {/* Botones de acción */}
                <button
                  style={styles.btnEdit}
                  onClick={() => alert("Función de editar próximamente")}
                >
                  ✏️
                </button>
                <button
                  style={styles.btnDelete}
                  onClick={() => handleDelete(product.id)}
                >
                  🗑️
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Paginación */}
      <div style={{ marginTop: "1rem" }}>
        {Array.from({ length: totalPages }, (_, i) => (
          <button
            key={i}
            onClick={() => setCurrentPage(i + 1)}
            style={{
              margin: "0 5px",
              padding: "0.3rem 0.6rem",
              backgroundColor: currentPage === i + 1 ? "#282c34" : "#eee",
              color: currentPage === i + 1 ? "#fff" : "#000",
              border: "none",
              cursor: "pointer",
            }}
          >
            {i + 1}
          </button>
        ))}
      </div>
    </div>
  );
}

const styles = {
  container: {
    padding: "2rem",
    textAlign: "center",
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
    marginTop: "1rem",
    backgroundColor: "#fff",
    boxShadow: "0 0 8px rgba(0,0,0,0.1)",
  },
  th: {
  backgroundColor: "#282c34",
  color: "#fff",           // Texto blanco sobre fondo oscuro
  padding: "0.75rem",
  borderBottom: "1px solid #ccc",
  textAlign: "left"
  },
  td: {
    padding: "0.75rem",
    borderBottom: "1px solid #ccc",
    color: "#000",           // <- Este es el ajuste clave
    backgroundColor: "#fff", // <- Asegura fondo blanco
    textAlign: "left"
  },
  btnEdit: {
    marginRight: "0.5rem",
    padding: "0.3rem",
    backgroundColor: "#ffc107",
    color: "#000",
    border: "none",
    cursor: "pointer",
    borderRadius: "4px",
  },
  btnDelete: {
    padding: "0.3rem",
    backgroundColor: "#dc3545",
    color: "#fff",
    border: "none",
    cursor: "pointer",
    borderRadius: "4px",
  },
};
